template.value:
def create
    @$var1$ = $var2$.new($var1$_params)

    respond_to do |format|
        if @$var1$.save
            flash[:notice] = '$var2$ was successfully created.'
            format.html { redirect_to(@$var1$) }
            format.xml  { render xml: @$var1$, status: :created, location: @$var1$ }
        else
            format.html { render action: 'new' }
            format.xml  { render xml: @$var1$.errors, status: :unprocessable_entity }
        end
    end
end

def update
    @$var1$ = $var2$.find(params[:id])

    respond_to do |format|
        if @$var1$.update($var1$_params)
            flash[:notice] = '$var2$ was successfully updated.'
            format.html { redirect_to(@$var1$) }
            format.xml  { head :ok }
        else
            format.html { render action: 'edit' }
            format.xml  { render xml: @$var1$.errors, status: :unprocessable_entity }
        end
    end
end
